  "source": "pragma solidity 0.4.23;\n\n/**\n * @title SafeMath\n * @dev Math operations with safety checks that throw on error\n */\nlibrary SafeMath {\n    function mul(uint256 a, uint256 b) internal pure returns (uint256) {\n        if (a == 0) {\n            return 0;\n        }\n        uint256 c = a * b;\n        assert(c / a == b);\n        return c;\n    }\n\n    function div(uint256 a, uint256 b) internal pure returns (uint256) {\n        // assert(b > 0); // Solidity automatically throws when dividing by 0\n        uint256 c = a / b;\n        // assert(a == b * c + a % b); // There is no case in which this doesn't hold\n        return c;\n    }\n\n    function sub(uint256 a, uint256 b) internal pure returns (uint256) {\n        assert(b <= a);\n        return a - b;\n    }\n\n    function add(uint256 a, uint256 b) internal pure returns (uint256) {\n        uint256 c = a + b;\n        assert(c >= a);\n        return c;\n    }\n}\n\n/**\n * @title Ownable\n * @dev The Ownable contract has an owner address, and provides basic authorization control\n * functions, this simplifies the implementation of \"user permissions\".\n */\ncontract Ownable {\n    address public owner;\n\n    /**\n      * @dev The Ownable constructor sets the original `owner` of the contract to the sender\n      * account.\n      */\n    constructor() public {\n        owner = msg.sender;\n    }\n\n    /**\n      * @dev Throws if called by any account other than the owner.\n      */\n    modifier onlyOwner() {\n        require(msg.sender == owner);\n        _;\n    }\n\n    /**\n    * @dev Allows the current owner to transfer control of the contract to a newOwner.\n    * @param newOwner The address to transfer ownership to.\n    */\n    function transferOwnership(address newOwner) public onlyOwner {\n        if (newOwner != address(0)) {\n            owner = newOwner;\n        }\n    }\n\n}\n\n\n/**\n * @title Pausable\n * @dev Base contract which allows children to implement an emergency stop mechanism.\n */\ncontract Pausable is Ownable {\n    event Pause();\n    event Unpause();\n\n    bool public paused = false;\n\n\n  /**\n   * @dev Modifier to make a function callable only when the contract is not paused.\n   */\n    modifier whenNotPaused() {\n        require(!paused);\n        _;\n    }\n\n  /**\n   * @dev Modifier to make a function callable only when the contract is paused.\n   */\n    modifier whenPaused() {\n        require(paused);\n        _;\n    }\n\n  /**\n   * @dev called by the owner to pause, triggers stopped state\n   */\n    function pause() onlyOwner whenNotPaused public {\n        paused = true;\n        emit Pause();\n    }\n\n  /**\n   * @dev called by the owner to unpause, returns to normal state\n   */\n    function unpause() onlyOwner whenPaused public {\n        paused = false;\n        emit Unpause();\n    }\n}\n\ncontract ERC20Basic {\n    uint public _totalSupply;\n    function totalSupply() public view returns (uint);\n    function balanceOf(address who) public view returns (uint);\n    function transfer(address to, uint value) public;\n    event Transfer(address indexed from, address indexed to, uint value);\n}\n\n/**\n * @title ERC20 interface\n * @dev see https://github.com/ethereum/EIPs/issues/20\n */\ncontract ERC20 is ERC20Basic {\n    function allowance(address owner, address spender) public view returns (uint);\n    function transferFrom(address from, address to, uint value) public;\n    function approve(address spender, uint value) public;\n    event Approval(address indexed owner, address indexed spender, uint value);\n}\n\n\ncontract BlockDebit is Pausable {\n    using SafeMath for uint;\n\n    struct Debit {\n        address addr;\n        bool isAlive;\n        uint inputWei;\n        uint shouldGiveBackTokens;\n        uint deadline;\n        uint nbDay;\n        uint interest;\n    }\n    mapping(address => Debit) public book;\n\n    address public ERC20TokenAddr;\n    uint public minMortgageAmount = 0.01e18;\n    uint public exchangeRate = 3076923;\n    uint public lendingLimit = 5000e6;\n\n    // nbDay => current interest, timestamp, interest after timestamp\n    mapping(uint => uint[3]) public interests;\n\n    event LogGiveDebit(address user, uint valueWei, uint shouldBackUSDT, uint deadline, uint nbDay, uint interest);\n    event LogBackDebit(address user, uint valueWei, uint shouldBackUSDT, uint deadline, uint nbDay, uint interest);\n\n    constructor(address _ERC20TokenAddr) public {\n        ERC20TokenAddr = _ERC20TokenAddr;\n        interests[7] = [500, 0, 500];\n        interests[15] = [1000, 0, 1000];\n        interests[30] = [2000, 0, 2000];\n        interests[60] = [4000, 0, 4000];\n    }\n\n    function updateERC20TokenAddr(address addr) public onlyOwner {\n        ERC20TokenAddr = addr;\n    }\n\n    function updateMinMortgageAmount(uint amount) public onlyOwner {\n        minMortgageAmount = amount;\n    }\n\n    function updateExchangeRate(uint rate) public onlyOwner {\n        exchangeRate = rate;\n    }\n\n    function updateInterest(uint nbDay, uint currentInterest, uint fromTimestamp, uint interest) public onlyOwner {\n        interests[nbDay] = [currentInterest, fromTimestamp, interest];\n    }\n\n    function deleteInterest(uint nbDay) public onlyOwner {\n        delete interests[nbDay];\n    }\n\n    function info(address addr) public view returns (uint[5]) {\n        Debit storage debitInfo = book[addr];\n        uint[5] memory a;\n        a[0] = debitInfo.inputWei;\n        a[1] = debitInfo.shouldGiveBackTokens;\n        a[2] = debitInfo.deadline;\n        a[3] = debitInfo.nbDay;\n        a[4] = debitInfo.interest;\n        return a;\n    }\n\n    function getInterest(uint nbDay) public view returns (uint[3]) {\n        return interests[nbDay];\n    }\n\n    // confiscate mortgages to owner address\n    function confiscateMortgages (address addr) public onlyOwner {\n        Debit storage debitInfo = book[addr];\n        require(debitInfo.isAlive == true);\n        require(block.timestamp > debitInfo.deadline);\n        // Avoid re-entryattacks\n        debitInfo.isAlive = false;\n\n        owner.transfer(debitInfo.inputWei);\n        delete book[addr];\n    }\n\n    function giveDebit(uint nbDay) public whenNotPaused payable {\n        // An address can only be loaned once\n        require(book[msg.sender].isAlive == false);\n        require(msg.value > minMortgageAmount);\n        require(interests[nbDay][0] > 0);\n\n        // Get interest\n        uint interest = interests[nbDay][0];\n        if (block.timestamp > interests[nbDay][1]) {\n            interest = interests[nbDay][2];\n        }\n\n        // Calculate the money that should be lent\n        uint equivalentTokens = msg.value.div(minMortgageAmount).mul(exchangeRate);\n        // Limited quantity\n        require(equivalentTokens <= lendingLimit);\n\n        // x = USDT * (10000- p) / 10000\n        uint shouldGiveTokens = equivalentTokens.mul(uint(10000).sub(interest)).div(10000);\n\n        uint deadline = block.timestamp.add(nbDay.mul(1 days));\n        book[msg.sender] = Debit(msg.sender, true, msg.value, equivalentTokens, deadline, nbDay, interest);\n\n        ERC20(ERC20TokenAddr).transferFrom(owner, msg.sender, shouldGiveTokens);\n\n        emit LogGiveDebit(msg.sender, msg.value, equivalentTokens, deadline, nbDay, interest);\n    }\n\n    function backDebit() public whenNotPaused {\n        Debit storage debitInfo = book[msg.sender];\n        require(debitInfo.isAlive == true);\n        require(block.timestamp <= debitInfo.deadline);\n        // Avoid re-entryattacks\n        debitInfo.isAlive = false;\n\n        ERC20(ERC20TokenAddr).transferFrom(msg.sender, owner, debitInfo.shouldGiveBackTokens);\n        msg.sender.transfer(debitInfo.inputWei);\n\n        emit LogBackDebit(msg.sender, debitInfo.inputWei, debitInfo.shouldGiveBackTokens,\n                          debitInfo.deadline, debitInfo.nbDay, debitInfo.interest);\n        // delete it\n        delete book[msg.sender];\n    }\n}\n",
